mapper.select:
SELECT



FROM chongwuyongpin  a



        and a.id in

        #{item}



        and a.chongwuyongpin_name like CONCAT('%',#{params.chongwuyongpinName},'%')


        and a.chongwuyongpin_uuid_number like CONCAT('%',#{params.chongwuyongpinUuidNumber},'%')


        and a.chongwuyongpin_types = #{params.chongwuyongpinTypes}


          and a.chongwuyongpin_kucun_number >= #{params.chongwuyongpinKucunNumberStart}   


          and a.chongwuyongpin_kucun_number <= #{params.chongwuyongpinKucunNumberEnd}   


        and a.chongwuyongpin_kucun_number = #{params.chongwuyongpinKucunNumber}


          and a.chongwuyongpin_old_money >= #{params.chongwuyongpinOldMoneyStart}   


          and a.chongwuyongpin_old_money <= #{params.chongwuyongpinOldMoneyEnd}   


          and a.chongwuyongpin_new_money >= #{params.chongwuyongpinNewMoneyStart}   


          and a.chongwuyongpin_new_money <= #{params.chongwuyongpinNewMoneyEnd}   


          and a.chongwuyongpin_clicknum >= #{params.chongwuyongpinClicknumStart}   


          and a.chongwuyongpin_clicknum <= #{params.chongwuyongpinClicknumEnd}   


        and a.chongwuyongpin_clicknum = #{params.chongwuyongpinClicknum}


        and a.chongwuyongpin_content like CONCAT('%',#{params.chongwuyongpinContent},'%')


        and a.shangxia_types = #{params.shangxiaTypes}


          and a.chongwuyongpin_delete >= #{params.chongwuyongpinDeleteStart}   


          and a.chongwuyongpin_delete <= #{params.chongwuyongpinDeleteEnd}   


        and a.chongwuyongpin_delete = #{params.chongwuyongpinDelete}


          and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 



order by a.${params.sort} ${params.order}
